// src/components/Header.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false); // To toggle the mobile menu

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen); // Toggle mobile menu visibility
  };

  return (
    <header className="bg-blue-500 text-white p-4">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-3xl font-bold">EchoGuard</h1> {/* Brand Name */}

        {/* Hamburger Menu (only visible on mobile) */}
        <div className="md:hidden">
          <button onClick={toggleMenu} className="text-2xl">
            <i className="fas fa-bars"></i> {/* Font Awesome Hamburger Icon */}
          </button>
        </div>

        {/* Navigation Links (visible on larger screens) */}
        <nav className="hidden md:flex space-x-6">
          <li>
            <Link to="/home" className="hover:text-gray-200">Home</Link>
          </li>
          <li>
            <Link to="/contact" className="hover:text-gray-200">Contact</Link>
          </li>
        </nav>
      </div>

      {/* Mobile Menu (Dropdown that appears when hamburger is clicked) */}
      {isMenuOpen && (
        <div className="absolute top-16 right-4 bg-white text-black p-4 shadow-lg rounded-md md:hidden">
          <ul className="space-y-4">
            <li>
              <Link to="/home" onClick={() => setIsMenuOpen(false)} className="block">Home</Link>
            </li>
            <li>
              <Link to="/contact" onClick={() => setIsMenuOpen(false)} className="block">Contact</Link>
            </li>
          </ul>
        </div>
      )}
    </header>
  );
};

export default Header;
