import React from 'react';
import bgVideo from '../assets/footerbg.mp4'; // Make sure you place the video in src/assets/

const Footer = () => {
  return (
    <footer className="relative w-full text-white py-10">
      {/* Background Video */}
      <video 
        autoPlay 
        loop 
        muted 
        className="absolute top-0 left-0 w-full h-full object-cover backdrop-blur-md"
      >
        <source src={bgVideo} type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      {/* Dark Overlay with Gradient */}
      <div className="absolute top-0 left-0 w-full h-full bg-gradient-to-t from-black via-black/80 to-purple-900/30"></div>

      {/* Content */}
      <div className="relative container mx-auto text-center px-6">
        <h1 className="text-xl font-bold font-serif drop-shadow-lg text-gray-200">
          Welcome to EchoGuard. EchoGuard is a safety app that uses voice recognition and real-time location tracking 
          to send emergency alerts to contacts and authorities, ensuring help is on the way in critical moments.
        </h1>
        <p className="mt-2 font-serif text-gray-300">&copy; 2025 EchoGuard. All rights reserved.</p>
      </div>
    </footer>
  );
};

export default Footer;
