// src/components/Header.jsx

import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false); // To toggle the menu visibility

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen); // Toggle menu visibility
  };

  return (
    <header className="bg-blue-600 text-white p-4 flex justify-between items-center">
      {/* Contact Info Section */}
      <div className="text-lg">
        <p>Contact: +1-234-567-890 | support@echoguard.com</p>
      </div>

      {/* Hamburger Menu Icon */}
      <div className="md:hidden">
        <button onClick={toggleMenu} className="text-2xl">
          &#9776; {/* This is the "three-line" icon (hamburger menu) */}
        </button>
      </div>

      {/* Navigation Menu for Larger Screens */}
      <nav className="hidden md:flex space-x-6">
        <Link to="/home" className="hover:text-gray-200">Home</Link>
        <Link to="/about" className="hover:text-gray-200">About</Link>
        <Link to="/contact" className="hover:text-gray-200">Contact</Link>
        <Link to="/settings" className="hover:text-gray-200">Settings</Link>
      </nav>

      {/* Dropdown Menu for Mobile */}
      {isMenuOpen && (
        <div className="absolute top-16 right-4 bg-white text-black p-4 shadow-lg rounded-md md:hidden">
          <ul className="space-y-4">
            <li><Link to="/home" onClick={() => setIsMenuOpen(false)} className="block">Home</Link></li>
            <li><Link to="/about" onClick={() => setIsMenuOpen(false)} className="block">About</Link></li>
            <li><Link to="/contact" onClick={() => setIsMenuOpen(false)} className="block">Contact</Link></li>
            <li><Link to="/settings" onClick={() => setIsMenuOpen(false)} className="block">Settings</Link></li>
          </ul>
        </div>
      )}
    </header>
  );
};

export default Header;
