// src/screens/HomeScreen.jsx

import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { MapContainer, TileLayer, Marker, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css"; // Import Leaflet CSS

// Component to get user's live location and update the map
const LocationMarker = () => {
  const [position, setPosition] = useState(null);
  const map = useMap();

  useEffect(() => {
    if (!navigator.geolocation) {
      alert("Geolocation is not supported by your browser.");
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (location) => {
        const { latitude, longitude } = location.coords;
        setPosition([latitude, longitude]);
        map.setView([latitude, longitude], 13); // Move map to user's location
      },
      () => {
        alert("Unable to retrieve your location.");
      }
    );
  }, [map]);

  return position === null ? null : <Marker position={position} />;
};

// Main Home Screen Component
const HomeScreen = () => {
  const location = useLocation();
  const userName = location.state?.name || "User"; // Get user's name from login

  const [showMap, setShowMap] = useState(false);
  const [isListening, setIsListening] = useState(false); // Track if microphone is on

  useEffect(() => {
    // Check if browser supports Speech API
    if (!("webkitSpeechRecognition" in window)) {
      alert("Your browser does not support voice recognition.");
      return;
    }

    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = true; // Keep listening continuously
    recognition.interimResults = false; // Don't return partial results
    recognition.lang = "en-US"; // Set language to English

    recognition.onstart = () => {
      setIsListening(true); // Show that mic is active
    };

    recognition.onend = () => {
      setIsListening(false); // Mic stopped listening
      recognition.start(); // Restart listening to keep it active
    };

    recognition.onresult = (event) => {
      const transcript = event.results[event.results.length - 1][0].transcript.trim().toLowerCase();
      console.log("Heard:", transcript); // Debugging: log what was heard

      if (transcript.includes("help me")) {
        alert("ðŸš¨ Emergency alert triggered via voice!");
        setShowMap(true); // Show map
      }
    };

    recognition.start(); // Start listening when component loads

    return () => {
      recognition.stop(); // Stop listening when component unmounts
    };
  }, []);

  return (
    <div className="min-h-screen flex flex-col bg-gray-100">
      {/* Header */}
      <header className="bg-blue-600 text-white p-4 flex justify-between items-center">
        <div className="text-xl">EchoGuard</div>
        <div className="text-lg">Hello, {userName}</div>
      </header>

      {/* Voice Status Indicator */}
      <div className="text-center text-sm text-gray-600 mt-2">
        ðŸŽ¤ {isListening ? "Listening for 'help me'..." : "Microphone off"}
      </div>

      {/* Main Content */}
      <main className="flex-grow flex flex-col justify-center items-center bg-white">
        {!showMap ? (
          <button
            onClick={() => {
              alert("Emergency alert triggered!");
              setShowMap(true);
            }}
            className="py-3 px-6 bg-red-600 text-white text-xl rounded-full"
          >
            Trigger Emergency Alert
          </button>
        ) : (
          <MapContainer center={[51.505, -0.09]} zoom={13} className="h-96 w-3/4 mt-6">
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
            <LocationMarker />
          </MapContainer>
        )}
      </main>

      {/* Footer */}
      <footer className="bg-blue-600 text-white p-4 text-center">
        <p>EchoGuard - Always listening, always protecting.</p>
      </footer>
    </div>
  );
};

export default HomeScreen;
