// src/screens/LoginScreen.jsx

import React, { useState } from 'react';
//import footer 
import Footer from './components/Footer';

const LoginScreen = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    contact: '',
    password: '',
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add logic for form submission (e.g., validation, backend API call)
    console.log(formData);
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-200">
      <div className="w-96 p-6 bg-white rounded shadow-md">
        <h2 className="text-2xl text-center mb-4">Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="name" className="block">Name</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className="w-full p-2 mt-1 border rounded"
            />
          </div>
          <div className="mb-4">
            <label htmlFor="email" className="block">Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="w-full p-2 mt-1 border rounded"
            />
          </div>
          <div className="mb-4">
            <label htmlFor="contact" className="block">Contact</label>
            <input
              type="text"
              name="contact"
              value={formData.contact}
              onChange={handleChange}
              className="w-full p-2 mt-1 border rounded"
            />
          </div>
          <div className="mb-4">
            <label htmlFor="password" className="block">Password</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className="w-full p-2 mt-1 border rounded"
            />
          </div>
          <div className="mb-4">
            <button type="submit" className="w-full py-2 bg-blue-500 text-white rounded">Login</button>
          </div>
        </form>
      </div>
      <Footer/>
    </div>

  );
};

export default LoginScreen;
